mapper.select:
Select c.id as contact_id, c.phone_number as contact_phone, c.email as contact_email, c.website as contact_website
from Contacts c
where c.id = #{id}

Select c.id as contact_id, c.phone_number as contact_phone, c.email as contact_email, c.website as contact_website
from Contacts c
where c.phone_number = #{phoneNumber}
